    tos = h;
    if (eachEntry) {
      push(p6);
      Eval();
      p1 = pop();
    } else {
      p1 = symbol(NIL);
    }
    set_binding(symbol(METAX), pop());
    set_binding(symbol(METAB), pop());
    set_binding(symbol(METAA), pop());
